import React, { Component } from 'react';
import { render } from "react-dom";
import { Link, Redirect } from 'react-router-dom';
import axios from 'axios';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Paper from '@material-ui/core/Paper';
import "./styles/loc.css"


const apiLink = '';

class Location extends Component {
    constructor(props) {
        super(props);
        this.state = {
          dis: 0,
        }
      this.distance = this.distance.bind(this);
      this.componentDidMount = this.componentDidMount.bind(this);
      this.haversine_distance = this.haversine_distance.bind(this);
    }

distance(lat1, lon1, lat2, lon2, unit) {
    	if ((lat1 == lat2) && (lon1 == lon2)) {
        console.log("Distance is :", "0");
    		return 0;
    	}
    	else {
    		var radlat1 = Math.PI * lat1/180;
    		var radlat2 = Math.PI * lat2/180;
    		var theta = lon1-lon2;
    		var radtheta = Math.PI * theta/180;
    		var dist = Math.sin(radlat1) * Math.sin(radlat2) + Math.cos(radlat1) * Math.cos(radlat2) * Math.cos(radtheta);
    		if (dist > 1) {
    			dist = 1;
    		}
    		dist = Math.acos(dist);
    		dist = dist * 180/Math.PI;
    		dist = dist * 60 * 1.1515;
    		if (unit=="K") { dist = dist * 1.609344 }
    		if (unit=="N") { dist = dist * 0.8684 }
        console.log("Distance is :", dist);
    		return dist;
    	}
}

componentDidMount(props) {
      navigator.geolocation.getCurrentPosition(function(position) {
        //var distance = distance(position.coords.latitude, position.coords.longitude, 48.8567, 2.3508, 'K');

        var distance = navigator.geolocation.distance(position.coords.latitude, position.coords.longitude, 48.8567, 2.3508, 'M');
        console.log("Latitude is :", position.coords.latitude);
        console.log("Longitude is :", position.coords.longitude);
        console.log("Longitude is :", distance);
      });
}


render() {
        return (
            <div className="container">
                <Typography variant="h2">
                    Vending Machine Locator
                </Typography>
                <Paper className="paper">
                    <Typography variant="h4">
                        My Location
                    </Typography>
                </Paper>
            </div>
        )
    }
}
render(<Location />, document.getElementById("root"));

export default (Location);
